def get_repos():
    gh = github3.login('DebuggerIssuesReport', password='DebuggerIssuesReport1')
    repos_for_prediction = []
    for ind, user in enumerate(reversed(github_users.split()[:5])):
        repos = list(gh.search_repositories('user:{0} language:Java'.format(user)))
        repos_for_prediction.extend(map(lambda x: x[0], filter(lambda repo: len(repo[1]) > 100, map(lambda repo: (repo, list(repo.repository.issues(state='all'))), repos))))
    return repos_for_prediction


if __name__ == "__main__":
    repos = get_repos()
